int main(int argc, char *argv[]) {

    char *exec_argv[argc + 2];
    exec_argv[0] = "strace";
    exec_argv[1] = "-T";
    for (size_t i = 2; i <= argc + 1; i++) {
        if (i == argc + 1) {
            exec_argv[i] = NULL;
            break;
        }
        exec_argv[i] = argv[i - 1];
    }
    char *exec_envp[] = { "PATH=/bin", NULL, };

    int fd[2];
    char buf[BUF_SIZE];
    ssize_t nread;
    pid_t pid = fork();

    if (pid == -1) {
        perror("fork failed"); 
        close(fd[0]);
        close(fd[1]);
        exit(EXIT_FAILURE);

    } else if (pid == 0) {
        printf("子进程 | PID：%d | 父进程PID：%d\n", getpid(), getppid());

        close(fd[0]); // 关闭读端
        dup2(fd[1], STDOUT_FILENO); // 重定向stdout
        close(fd[1]); // 关闭原写端

        execve("/bin/strace", exec_argv, exec_envp);

        // 若execvp失败，打印错误并退出
        perror("execvp failed");
        exit(EXIT_FAILURE);
    } else {
        printf("父进程 | PID：%d | 子进程PID：%d\n", getpid(), pid);

        close(fd[1]); // 关闭写端
        printf("Parent reading 'strace -T' output:\n");
        printf("========================================\n");
        while ((nread = read(fd[0], buf, BUF_SIZE)) > 0) {
            write(STDOUT_FILENO, buf, nread);

            }
        printf("========================================\n");
        printf("Parent: 'strace -T' output read complete.\n");
    }


    return 0;
}
